export const mathematicsPaper2UrduQuestions = rawMathematicsPaper2UrduData.map(
  (question, index) => {
    const options = question.options ?? [];
    const normalizedCorrectAnswer =
      Number.isInteger(question.correctAnswer) &&
      question.correctAnswer >= 0 &&
      question.correctAnswer < options.length
        ? question.correctAnswer
        : 0;

    return {
      id: `URDU-MATH2-${index + 1}`,
      question: question.question ?? "",
      options,
      correctAnswer: normalizedCorrectAnswer,
      explanation: question.explanation ?? "",
    };
  },
);


export const convertMathematicsPaper2UrduToQuestionFormat = (
  questions: typeof mathematicsPaper2UrduQuestions,
) => {
  return questions.map((question, index) => ({
    id: index + 1,
    question: question.question,
    options: question.options,
    correctAnswer: question.correctAnswer,
    explanation: question.explanation,
  }));
};